mapper.insert:
INSERT INTO tbl_article (
    article_no
    , title
    , content
    , writer
    , file_cnt
) VALUES (
    #{articleNo}
    , #{title}
    , #{content}
    , #{writer}
    , #{fileCnt}
)

mapper.update:
UPDATE tbl_article
SET
    title = #{title}
    , content = #{content}
    , file_cnt = #{fileCnt}
WHERE
    article_no = #{articleNo}

UPDATE tbl_article
SET reply_cnt = reply_cnt + #{amount}
WHERE article_no = #{articleNo}

UPDATE tbl_article
SET view_cnt = view_cnt + 1
WHERE article_no = #{articleNo}
