mapper.insert:
INSERT INTO tbl_article_file (
    file_name
    , article_no
) VALUES (
    #{fileName}
    , LAST_INSERT_ID()
)

INSERT INTO tbl_article_file (
    file_name
    , article_no
) VALUES (
    #{fileName}
    , #{articleNo}
)

mapper.update:
UPDATE tbl_article
SET file_cnt = (
    SELECT COUNT(article_no)
    FROM tbl_article_file
    WHERE article_no = #{articleNo}
)
WHERE article_no = #{articleNo}
